import { useEffect, useState } from "react";
import { fetchSets, createSet, renameSet, toggleSet } from "@/api";
import { Card, CardTitle } from "@/components/Card";
import { Table, Thead, Th, Tbody, Td } from "@/components/Table";
import { useToast } from "@/components/Toast";
import { getRole } from "@/auth";

type SetItem = { id:number; name:string; is_active:boolean };

export default function Sets(){
  const [sets,setSets]=useState<SetItem[]>([]);
  const [name,setName]=useState("");
  const { push } = useToast();
  const role = getRole();

  const load=async()=>{ try{ setSets(await fetchSets()); }catch(e:any){ push({msg:e.message,type:"err"});} };
  useEffect(()=>{ load(); },[]);

  if(role!=="admin") return <div className="card">You need admin access.</div>;

  return (
    <div className="grid gap-6">
      <Card>
        <CardTitle>Create set</CardTitle>
        <div className="flex gap-3 mt-2">
          <input placeholder="name (e.g., s)" value={name} onChange={e=>setName(e.target.value)}/>
          <button className="btn rounded-xl" onClick={async()=>{ if(!name) return; await createSet(name); setName(""); await load(); push({msg:"Set created",type:"ok"}); }}>Add</button>
        </div>
      </Card>

      <Card>
        <CardTitle>All sets</CardTitle>
        <div className="overflow-auto">
          <Table>
            <Thead>
              <Th>ID</Th><Th>Name</Th><Th>Active</Th><Th>Actions</Th>
            </Thead>
            <Tbody>
              {sets.map(s=>
                <tr key={s.id}>
                  <Td>{s.id}</Td>
                  <Td>{s.name}</Td>
                  <Td>{s.is_active?"Yes":"No"}</Td>
                  <Td>
                    <div className="flex gap-2">
                      <button className="px-3 py-2 rounded-xl border border-line" onClick={async()=>{ const n=prompt("New name", s.name); if(!n) return; await renameSet(s.id,n); await load(); }}>Rename</button>
                      <button className="px-3 py-2 rounded-xl border border-line" onClick={async()=>{ await toggleSet(s.id, !s.is_active); await load(); }}>{s.is_active?"Disable":"Enable"}</button>
                    </div>
                  </Td>
                </tr>
              )}
            </Tbody>
          </Table>
        </div>
      </Card>
    </div>
  );
}

web/src/routes/Upload.tsx.content:
import { useEffect, useState } from "react";
import { fetchSets, uploadBet, updateBet, deleteBet, importCSV } from "@/api";
import Dropzone from "@/components/Dropzone";
import { Card, CardTitle } from "@/components/Card";
import { useToast } from "@/components/Toast";

type SetItem = { id:number; name:string; is_active:boolean };

export default function Upload(){
  const [sets,setSets]=useState<SetItem[]>([]);
  const [setId,setSetId]=useState<number|undefined>();
  const [bookmaker,setBk]=useState("Sportsbet");
  const [stake,setStake]=useState("");
  const [file,setFile]=useState<File|null>(null);
  const [preview,setPreview]=useState<string|undefined>();
  const [out,setOut]=useState<any>(null);
  const [showConfirmModal, setShowConfirmModal] = useState(false);
  const [uploadResponse, setUploadResponse] = useState<any>(null);
  const [csvFile, setCsvFile] = useState<File|null>(null);
  const [csvResult, setCsvResult] = useState<any>(null);
  const [csvSetId, setCsvSetId] = useState<number|undefined>();
  const { push } = useToast();

  useEffect(()=>{ (async()=>{
    try{
      const s = await fetchSets(true) as SetItem[];
      setSets(s); 
      if(s.length) {
        setSetId(s[0].id);
        setCsvSetId(s[0].id); // Also set default for CSV
      }
    }catch(e:any){ push({ msg: e.message, type:"err"}); }
  })(); }, []);

  const submit = async(e:React.FormEvent) => {
    e.preventDefault();
    if(!file || !setId || !stake){ push({ msg:"All fields required", type:"err"}); return; }
    const fd = new FormData();
    fd.append("set_id", String(setId));
    fd.append("bookmaker_name", bookmaker);
    fd.append("stake_manual", stake);
    fd.append("image", file);
    try{
      const resp = await uploadBet(fd);
      setUploadResponse(resp);
      setShowConfirmModal(true);
      // Don't show immediate toast, let user confirm data first
    }catch(err:any){ push({ msg: err.message || "Upload failed", type:"err"}); }
  };

  const handleCSVUpload = async (dryRun: boolean = false) => {
    if (!csvFile) {
      push({ msg: "Please select a CSV file", type: "err" });
      return;
    }
    if (!csvSetId) {
      push({ msg: "Please select a set for the CSV import", type: "err" });
      return;
    }

    try {
      const selectedSet = sets.find(s => s.id === csvSetId);
      const defaultSet = selectedSet ? selectedSet.name : String(csvSetId);
      const result = await importCSV(csvFile, dryRun, defaultSet);
      setCsvResult(result);
      if (dryRun) {
        push({ msg: `Dry run complete: ${result.total_rows} rows validated`, type: "ok" });
      } else {
        push({ msg: `CSV imported: ${result.inserted} rows added, ${result.skipped} skipped`, type: "ok" });
      }
    } catch (err: any) {
      push({ msg: err.message || "CSV import failed", type: "err" });
    }
  };

  return (
    <div className="grid gap-6">
      <Card>
        <CardTitle>Upload betslip</CardTitle>
        <form className="grid md:grid-cols-2 gap-4 mt-2" onSubmit={submit}>
          <label className="grid gap-1">
            <span className="text-sm text-mut">Set</span>
            <select value={setId} onChange={e=>setSetId(Number(e.target.value))}>
              {sets.map(s=><option key={s.id} value={s.id}>{s.name}</option>)}
            </select>
          </label>
          <label className="grid gap-1">
            <span className="text-sm text-mut">Bookmaker</span>
            <input value={bookmaker} onChange={e=>setBk(e.target.value)} />
          </label>
          <label className="grid gap-1">
            <span className="text-sm text-mut">Stake (manual)</span>
            <input type="number" step="0.01" value={stake} onChange={e=>setStake(e.target.value)} />
          </label>
          <div className="grid gap-2">
            <span className="text-sm text-mut">Screenshot</span>
            <Dropzone onFile={(f)=>{ setFile(f); setPreview(URL.createObjectURL(f)); }} />
            <input type="file" accept="image/*" onChange={e=>{ const f=e.target.files?.[0]; if(f){ setFile(f); setPreview(URL.createObjectURL(f)); }}}/>
          </div>
          <div className="md:col-span-2">
            <button className="btn px-4 py-2 rounded-xl">Upload</button>
          </div>
        </form>
      </Card>

      {preview && <Card><CardTitle>Preview</CardTitle><img src={preview} className="rounded-xl border border-line mt-2 max-h-[360px] object-contain"/></Card>}

      {/* CSV Import Card */}
      <Card>
        <CardTitle>Import CSV</CardTitle>
        <div className="mt-2 space-y-4">
          <div>
            <span className="text-sm text-mut block mb-2">
              Upload a CSV with columns: <code>set,profit</code> or <code>stake,odds,profit</code>
            </span>
            <input 
              type="file" 
              accept=".csv" 
              onChange={(e) => setCsvFile(e.target.files?.[0] || null)}
              className="block w-full text-sm text-gray-400
                         file:mr-4 file:py-2 file:px-4
                         file:rounded file:border-0
                         file:text-sm file:font-medium
                         file:bg-blue-600 file:text-white
                         hover:file:bg-blue-700"
            />
          </div>

          <div>
            <label className="grid gap-1">
              <span className="text-sm text-mut">Set (for CSV without set column)</span>
              <select value={csvSetId} onChange={e=>setCsvSetId(Number(e.target.value))}>
                {sets.map(s=><option key={s.id} value={s.id}>{s.name}</option>)}
              </select>
            </label>
          </div>

          <div className="flex gap-2">
            <button 
              type="button"
              onClick={() => handleCSVUpload(true)}
              className="btn px-4 py-2 rounded-xl bg-gray-600 hover:bg-gray-700"
              disabled={!csvFile || !csvSetId}
            >
              Preview (Dry Run)
            </button>
            <button 
              type="button"
              onClick={() => handleCSVUpload(false)}
              className="btn px-4 py-2 rounded-xl bg-green-600 hover:bg-green-700"
              disabled={!csvFile || !csvSetId}
            >
              Import CSV
            </button>
          </div>

          {csvResult && (
            <div className="mt-4 p-4 bg-[#0f1721] rounded border">
              <h4 className="font-medium mb-2">Import Result</h4>
              <div className="text-sm space-y-1">
                <div>File: <span className="text-blue-400">{csvResult.filename}</span></div>
                <div>Total rows: <span className="text-yellow-400">{csvResult.total_rows}</span></div>
                <div>Inserted: <span className="text-green-400">{csvResult.inserted}</span></div>
                <div>Skipped: <span className="text-red-400">{csvResult.skipped}</span></div>
                {csvResult.errors && csvResult.errors.length > 0 && (
                  <div>
                    <div className="text-red-400 mt-2">Errors:</div>
                    <ul className="list-disc list-inside text-xs text-red-300 ml-2">
                      {csvResult.errors.map((error: any, idx: number) => (
                        <li key={idx}>Row {error.row}: {error.error}</li>
                      ))}
                    </ul>
                  </div>
                )}
              </div>
            </div>
          )}
        </div>
      </Card>

      {out && <Card><CardTitle>Parsed result</CardTitle><pre className="mt-2 text-sm overflow-auto">{JSON.stringify(out,null,2)}</pre></Card>}

      {/* Confirmation Modal */}
      {showConfirmModal && uploadResponse && (
        <ConfirmationModal 
          response={uploadResponse}
          onConfirm={(correctedData) => {
            setOut(correctedData);
            setShowConfirmModal(false);
            push({ msg: "Bet uploaded successfully!", type: "ok" });
          }}
          onCancel={async () => {
            try {
              await deleteBet(uploadResponse.id);
              setShowConfirmModal(false);
              push({ msg: "Upload cancelled and removed", type: "ok" });
            } catch (error: any) {
              console.error("Failed to delete bet:", error);
              setShowConfirmModal(false);
              push({ msg: "Upload cancelled (could not remove from server)", type: "err" });
            }
          }}
        />
      )}
    </div>
  );
}

// Confirmation Modal Component
interface ConfirmationModalProps {
  response: any;
  onConfirm: (data: any) => void;
  onCancel: () => void;
}

function ConfirmationModal({ response, onConfirm, onCancel }: ConfirmationModalProps) {
  const [editedStake, setEditedStake] = useState(response.stake_manual || "");
  const [editedOdds, setEditedOdds] = useState(response.odds_numeric || "");
  const [editedReturn, setEditedReturn] = useState(response.potential_return || "");
  const [editedResult, setEditedResult] = useState(response.result_status || "");
  const [editedBetType, setEditedBetType] = useState(response.bet_type || "");

  const handleConfirm = async () => {
    try {
      // Fix numeric parsing to handle valid zero values
      const stakeValue = parseFloat(editedStake);
      const oddsValue = parseFloat(editedOdds);
      const returnValue = parseFloat(editedReturn);

      const updates = {
        stake_manual: isNaN(stakeValue) ? response.stake_manual : stakeValue,
        odds_numeric: isNaN(oddsValue) ? response.odds_numeric : oddsValue,
        potential_return: isNaN(returnValue) ? response.potential_return : returnValue,
        result_status: editedResult || response.result_status,
        bet_type: editedBetType || response.bet_type,
      };

      // Call API to persist changes
      const updatedBet = await updateBet(response.id, updates);
      onConfirm(updatedBet);
    } catch (error: any) {
      console.error("Failed to update bet:", error);
      alert("Failed to save changes: " + (error.message || "Unknown error"));
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-[#1a2332] p-6 rounded-xl max-w-4xl w-full mx-4 max-h-[90vh] overflow-y-auto">
        <h2 className="text-xl font-bold mb-4 text-white">Confirm Extracted Data</h2>

        <div className="grid md:grid-cols-2 gap-6">
          {/* Left side: Original Image and OCR Text */}
          <div className="space-y-4">
            <div>
              <h3 className="font-medium mb-2 text-white">Uploaded Image</h3>
              <div className="bg-[#0f1721] p-4 rounded">
                {response.image_path ? (
                  <img 
                    src={`/files/${response.image_path}`} 
                    alt="Betting slip"
                    className="max-w-full h-auto rounded"
                    style={{ maxHeight: '300px' }}
                  />
                ) : (
                  <div className="text-center text-gray-400 py-8">No image available</div>
                )}
              </div>
            </div>

            <div>
              <h3 className="font-medium mb-2 text-white">OCR Extracted Text</h3>
              <div className="bg-[#0f1721] p-4 rounded max-h-40 overflow-y-auto">
                <pre className="text-sm text-gray-300 whitespace-pre-wrap">
                  {response.event_text || response.ocr_text || "No OCR text available"}
                </pre>
              </div>
            </div>
          </div>

          {/* Right side: Editable Fields */}
          <div className="space-y-4">
            <h3 className="font-medium mb-2 text-white">Extracted Bet Details</h3>

            <div className="space-y-4">
              <label className="grid gap-1">
                <span className="text-sm text-gray-300">Stake ($)</span>
                <input 
                  type="number" 
                  step="0.01"
                  value={editedStake}
                  onChange={(e) => setEditedStake(e.target.value)}
                  className="bg-[#0f1721] border border-gray-600 rounded px-3 py-2 text-white"
                  placeholder="Enter stake amount"
                />
              </label>

              <label className="grid gap-1">
                <span className="text-sm text-gray-300">Odds</span>
                <input 
                  type="number" 
                  step="0.01"
                  value={editedOdds}
                  onChange={(e) => setEditedOdds(e.target.value)}
                  className="bg-[#0f1721] border border-gray-600 rounded px-3 py-2 text-white"
                  placeholder="Enter odds"
                />
              </label>

              <label className="grid gap-1">
                <span className="text-sm text-gray-300">Potential Return ($)</span>
                <input 
                  type="number" 
                  step="0.01"
                  value={editedReturn}
                  onChange={(e) => setEditedReturn(e.target.value)}
                  className="bg-[#0f1721] border border-gray-600 rounded px-3 py-2 text-white"
                  placeholder="Enter return amount"
                />
              </label>

              <label className="grid gap-1">
                <span className="text-sm text-gray-300">Result Status</span>
                <select 
                  value={editedResult}
                  onChange={(e) => setEditedResult(e.target.value)}
                  className="bg-[#0f1721] border border-gray-600 rounded px-3 py-2 text-white"
                >
                  <option value="">Select result</option>
                  <option value="won">Won</option>
                  <option value="lost">Lost</option>
                  <option value="pending">Pending</option>
                  <option value="void">Void</option>
                  <option value="cashout">Cash Out</option>
                </select>
              </label>

              <label className="grid gap-1">
                <span className="text-sm text-gray-300">Bet Type</span>
                <input 
                  type="text"
                  value={editedBetType}
                  onChange={(e) => setEditedBetType(e.target.value)}
                  className="bg-[#0f1721] border border-gray-600 rounded px-3 py-2 text-white"
                  placeholder="e.g., single, multi, system"
                />
              </label>
            </div>

            {/* Action Buttons */}
            <div className="flex gap-3 pt-4">
              <button 
                onClick={handleConfirm}
                className="bg-green-600 hover:bg-green-700 text-white px-6 py-2 rounded-lg font-medium"
              >
                Confirm & Save
              </button>
              <button 
                onClick={onCancel}
                className="bg-gray-600 hover:bg-gray-700 text-white px-6 py-2 rounded-lg font-medium"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

